mapper.select:
select
  id, name, action_type, action, action_remark, testcase_id, page_id, element_id, option_data, type, create_by, create_time, update_by, update_time, sort
from galloping_snail.t_step_ui_new


        and id = #{id}


        and name = #{name}


        and action_type = #{actionType}


        and action = #{action}


        and action_remark = #{actionRemark}


        and testcase_id = #{testcaseId}


        and page_id = #{pageId}


        and element_id = #{elementId}


        and option_data = #{optionData}


        and type = #{type}


        and create_by = #{createBy}


        and create_time = #{createTime}


        and update_by = #{updateBy}


        and update_time = #{updateTime}


        and sort = #{sort}
